import { useEffect, useState } from 'react';

function App() {
    const [loading, setLoading] = useState(true);
    const [movies, setMovies] = useState([]);
    const getMovies = async () => {
        const json = await (await fetch('https://yts.mx/api/v2/list_movies.json?minimum_rating=9&sort_by=year')).json();
        setMovies(json.data.movies);
        setLoading(false);
    };
    useEffect(() => {
        getMovies();
    }, []);

    console.log(movies);
    return (
        <div>
            <div>
                {loading ? (
                    <h1>Loading...</h1>
                ) : (
                    <div>
                        {movies.map((movie) => (
                            <div key={movie.id}>
                                <img src={movie.medium_cover_image} alt='thumbnail' />
                                <h2>{movie.title}</h2>
                                <p>{movie.summary}</p>
                                <ul>
                                    <li>
                                        {movie.genres.map((g) => (
                                            <li key={g}>{g}</li>
                                        ))}
                                    </li>
                                </ul>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
