import { useState, useEffect } from 'react';
import Movie from '../components/Movie';
import styled from 'styled-components';

const HomeWrapper = styled.div`
    width: 100%;
    background-color: #020202;
`;

const Center = styled.div`
    width: 1310px;
    margin: 0 auto;
`;

const Header = styled.header`
    width: 100%;
    height: 100px;
`;

const Gnb = styled.nav`
    width: 1310px;
    margin: auto 0;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`;

const LogoImg = styled.img``;

const SignInBtn = styled.button`
    height: 36px;
    padding: 0px 16px;
    border-radius: 10px;
    background: #e30914;
`;

const Main = styled.main`
    width: 100%;
    height: 100%;
`;

const Banner = styled.section`
    width: 100%;
    height: 946px;
`;

const TitleBox = styled.div``;

const MovieTitle = styled.img``;

const MovieDesc = styled.p`
    color: #777;
    font-size: 14px;
`;

const MoreBtn = styled.button`
    height: 50px;
    padding: 10px 30px;
    text-align: center;
    line-height: 50px;
    color: #777;
    border-radius: 10px;
    border: 1px solid #777;
`;

const MovieWrapper = styled.div``;

const Category = styled.div``;

const CategoryTitle = styled.h2`
    color: #ccc;
    font-size: 20px;
    font-family: Roboto;
    line-height: 100%;
`;

function Home() {
    const [loading, setLoading] = useState(true);
    const [movies, setMovies] = useState([]);
    const getMovies = async () => {
        const json = await (await fetch('https://yts.mx/api/v2/list_movies.json?minimum_rating=9&sort_by=year')).json();
        setMovies(json.data.movies);
        setLoading(false);
    };
    useEffect(() => {
        getMovies();
    }, []);

    return (
        <HomeWrapper>
            <Header>
                <Gnb>
                    <LogoImg src='' alt='' />
                    <SignInBtn>Sign In</SignInBtn>
                </Gnb>
            </Header>
            <Main>
                <Banner>
                    <Center>
                        <TitleBox>
                            <MovieTitle />
                            <MovieTitle />
                            <MovieDesc></MovieDesc>
                            <MoreBtn>View More</MoreBtn>
                        </TitleBox>
                    </Center>
                </Banner>
                <MovieWrapper>
                    <Category>
                        <Center>
                            <CategoryTitle>Popular on STUDIOS</CategoryTitle>
                        </Center>
                    </Category>
                </MovieWrapper>
            </Main>
            <div>
                {loading ? (
                    <h1>Loading...</h1>
                ) : (
                    <div>
                        {movies.map((movie) => (
                            <Movie key={movie.id} id={movie.id} coverImg={movie.medium_cover_image} title={movie.title} summary={movie.summary} genres={movie.genres} />
                        ))}
                    </div>
                )}
            </div>
        </HomeWrapper>
    );
}
export default Home;
