import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const GridContainer = styled.div``;

const GridItem = styled.div`
    /* width: 230px; */
    position: relative;
    border: 1px solid red;
`;

const MoviePoster = styled.img`
    width: 100%;
`;

const MovieDescBox = styled.div`
    position: absolute;
    width: 100%;
    height: 100%;
    bottom: 0;
    right: 0;
    background: linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.9) 60%);
    display: flex;
    flex-direction: column;
    justify-content: end;
`;

const MovieTitle = styled.h2`
    & Link {
        white-space: nowrap;
        word-break: break-all;
        text-overflow: ellipsis;
        font-size: 100px;
    }
    white-space: pre-wrap;
    word-break: break-all;
    text-overflow: ellipsis;
    font-size: 20px;
`;

const MovieRate = styled.div``;

const MovieDesc = styled.p`
    font-size: 14px;
    white-space: pre-wrap;
    text-overflow: ellipsis;
    font-weight: 300;
    color: #ccc;
`;
function Movie({ id, coverImg, title, summary, rating }) {
    return (
        <GridContainer>
            <GridItem>
                <MoviePoster src={coverImg} alt={title} />
                <MovieDescBox>
                    <MovieTitle>
                        <Link to={`/movie/${id}`}>{title}</Link>
                    </MovieTitle>
                    <MovieRate>{rating}</MovieRate>
                    <MovieDesc>{summary.length > 235 ? `${summary.slice(0, 235)}...` : summary}</MovieDesc>
                </MovieDescBox>
            </GridItem>
        </GridContainer>
    );
}

Movie.propTypes = {
    id: PropTypes.number.isRequired,
    coverImg: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    summary: PropTypes.string.isRequired,
    rating: PropTypes.number.isRequired,
};
export default Movie;
